<template>
	<view class="billList">
		<z-paging
			:paging-style="{ marginTop: '0' }"
			ref="paging"
			use-virtual-list
			:force-close-inner-list="true"
			:auto="true"
			:refresher-enabled="true"
			:cell-height-mode="0 != 0 ? 'fixed' : 'dynamic'"
			@virtualListChange="virtualListChange"
			@query="queryList"
			style=""
		>
			<view v-for="(item, index) in orderList" :key="index" :index="index" @click="goPath('/pages/subOrder/details?id=' + item.id)" class="OrderCard">
				<view class="OrderCardHand" @tap.stop>
					<view class="title ml1 flex-1" style="" @tap.stop>
						<text class="ft30 ft-lighgray pr30" style="color: #666666" @tap.stop>
							订单编号:
							<text class="ml15" style="color: #f76565">
								{{ item.orderNumber }}
							</text>
						</text>
					</view>
					<view class="ml20" style="margin-right: -20rpx">
						<u-image v-if="pinia_userRole == 'D' && item.paymentState == '0'" class="u-img" width="120rpx" height="50rpx" src="@/static/img/obj/bq1.png"></u-image>
						<u-image v-if="pinia_userRole == 'R' && item.paymentState == '0'" class="u-img" width="120rpx" height="50rpx" src="@/static/img/obj/dqs.png"></u-image>
						<u-image v-if="item.paymentState == '1'" width="120rpx" height="50rpx" class="u-img" src="@/static/img/obj/bq2.png" :lazy-load="true"></u-image>
						<u-image
							v-if="pinia_userRole != 'R' && item.paymentState == '2'"
							width="120rpx"
							height="50rpx"
							class="u-img"
							src="@/static/img/obj/bq3.png"
							:lazy-load="true"
						></u-image>
						<u-image v-if="pinia_userRole == 'R' && item.paymentState == '2'" class="u-img" width="120rpx" height="50rpx" src="@/static/img/obj/yfk.png"></u-image>
					</view>
				</view>
				<view class="width100 pb25 text-left">
					<text class="flex-col text-left">
						<text
							v-if="pinia_userRole == 'D'"
							:style="{ color: ifZX(item.bossNumberE) ? '#AAAAAA' : '#3D3D3D' }"
							class="ft34 u-line-bt width100"
							style="font-weight: 500"
						>
							{{ item.organizationE || item.bossNumberE }}{{ ifZX(item.bossNumberE) ? '(已注销)' : '' }}
						</text>
						<text
							v-if="pinia_userRole == 'R'"
							:style="{ color: ifZX(item.bossNumberE) ? '#AAAAAA' : '#3D3D3D' }"
							class="ft34 u-line-bt width100"
							style="font-weight: 500"
						>
							{{ item.enterpriseS || item.bossNumberS }}{{ ifZX(item.bossNumberS) ? '(已注销)' : '' }}
						</text>
					</text>
				</view>
				<text class="ft30 line25 ft-lighgray">
					<text>日期：{{ $u.timeFormat(item.creationTime, 'yyyy-mm-dd') }}</text>
				</text>

				<text class="ft-lighgray mt10 line25 flex-row items-center justify-end">
					<text>订单金额：</text>
					<text style="color: black; font-size: 12px">￥</text>
					<text class="ft35" style="color: black; font-weight: 500">{{ item.price.toFixed(2) }}</text>
				</text>
			</view>
		</z-paging>
	</view>
</template>

<script>
export default {
	data() {
		return {
			orderList: [],
			billsList: [],
			bat64: {
				dqs: 'data:image/png;base64,',
				yqs: 'data:image/png;base64,',
				dqss: 'data:image/png;base64,',
				ysk: 'data:image/png;base64,',
				yfk: 'data:image/png;base64,'
			}
		};
	},
	onLoad(option) {
		console.log(option);
		this.billsList = option.listId.split(',');
	},
	methods: {
		virtualListChange(vList) {
			this.orderList = vList;
		},
		queryList(pageNo, pageSize) {
			uni.$api.bills
				.getBillOrdersByIds({
					billList: this.billsList,
					page: pageNo,
					size: pageSize
				})
				.then((res) => {
					this.$refs.paging.complete(res.data.data);
				});
		},
		ifZX(val) {
			return val.includes('zx-');
		}
	}
};
</script>

